package first_code;

public class Nested_if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=100;
		int b=5000;
		int c=7000;
		if (a>b & a>c) 
		{
			System.out.println("a is bigger");
		}else if (b>a & b>c) 
		{
			System.out.println("b is bigger");
		}
		else
		{System.out.println(" c is bigger");}
	}

}
